import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      query: '',
      weatherinfo: []
    }
  }

  handleChange(e) {
    console.log(this.state.query);
    this.setState({ query: e.target.value });
  }

  componentDidMount() {
    fetch("someapicallurl")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({weatherinfo: [1,2,3]});
        },
        (error) => {
          console.log("error occurred while retrieving weather details");
        }
      )
  }

  render() {
    return (
      <div className="app">
        <div className="container f fd-c">

          <div className="header">
            <input
              className="search-input"
              type="text"
              value={this.state.query}
              onChange={this.handleChange.bind(this)}
            />
          </div>
          <div className="content f">
            <div className="tile">Yesterday</div>
            <div className="tile">Today</div>
            <div className="tile">Tomorrow</div>
          </div>

        </div>
      </div>
    );
  }
}

export default App;
