import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      query: '',
      weatherinfo: []
    }
  }

  handleChange(e) {
    console.log(this.state.query);
    //this.setState({ query: e.target.value });
    this.sendRequest();
  }

  sendRequest() {
    fetch("http://samples.openweathermap.org/data/2.5/weather?id=1264527&appid=2dbe93d485a7fc29365445a9669b0e72")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({weatherinfo: [1,2,3]});
        },
        (error) => {
          console.log("error occurred while retrieving weather details");
        }
      )
  }

  render() {
    return (
      <div className="app">
        <div className="container f fd-c">

          <div className="header">
            <input
              className="search-input"
              type="text"
              value={this.state.query}
              onChange={this.handleChange.bind(this)}
            />
          </div>
          <div className="content f">
            <div className="tile">Yesterday - {this.state.weatherinfo[0]}</div>
            <div className="tile">Today - {this.state.weatherinfo[1]}</div>
            <div className="tile">Tomorrow - {this.state.weatherinfo[2]}</div>
          </div>

        </div>
      </div>
    );
  }
}

export default App;
